import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Özel Marker İkonu
const customIcon = new L.Icon({
  iconUrl: '/marker.png',
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const Map = ({ tours, cities }) => {
  // Şehir adını koordinatlara eşleştiren fonksiyon
  const getCityCoordinates = (cityName) => {
    const city = cities.find((c) => c.name === cityName);
    return city ? [city.latitude, city.longitude] : null;
  };

  return (
    <div className="map-container">
      <MapContainer
        center={[41.0082, 28.9784]} // İstanbul koordinatları
        zoom={3}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />
        {tours.map((tour) =>
          tour.days.map((day) => {
            const today = new Date();
            const startDate = new Date(tour.start_date);
            const dayDifference = Math.floor((today - startDate) / (1000 * 60 * 60 * 24)) + 1; // Kaçıncı gün hesaplama
            const coordinates = getCityCoordinates(day.city); // Şehir koordinatlarını al

            if (day.day_number === dayDifference && coordinates) {
              return (
                <Marker
                  key={`${tour.id}-${day.day_number}`}
                  position={coordinates}
                  icon={customIcon}
                >
                  <Popup>
                    <h3>{tour.name}</h3>
                    <img
                      src={tour.image}
                      alt={`${tour.name} görseli`}
                      style={{ width: '100%', height: 'auto', marginBottom: '10px' }}
                    />
                    <p>Rehber: {tour.guide}</p>
                    <p>
                      <strong>Kaçıncı Gün:</strong> {day.day_number}
                    </p>
                    <p>Şehir: {day.city}</p>
                    <p>Açıklama: {day.description}</p>
                    <p>Başlangıç: {new Date(tour.start_date).toLocaleDateString()}</p>
                    <p>Bitiş: {new Date(tour.end_date).toLocaleDateString()}</p>
                  </Popup>
                </Marker>
              );
            }
            return null;
          })
        )}
      </MapContainer>
    </div>
  );
};

export default Map;
