import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Özel Marker İkonu
const customIcon = new L.Icon({
  iconUrl: '/marker.png', // Eğer public klasöründeyse
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const Map = ({ tours }) => {
  return (
    <div className="app-container">
      {/* Liste Görünümü */}
      <div className="list-container">
        <h2>Tur Listesi</h2>
        <ul>
          {tours.map((tour) => (
            <li key={tour.id} className="tour-item">
              <h3>{tour.name}</h3>
              <p>Rehber: {tour.guide}</p>
              <p>Başlangıç Tarihi: {new Date(tour.start_date).toLocaleDateString()}</p>
              <p>Bitiş Tarihi: {new Date(tour.end_date).toLocaleDateString()}</p>
              <img
                src={tour.image}
                alt={`${tour.name} görseli`}
                style={{ width: '100px', height: 'auto' }}
              />
              <ul>
                {tour.days.map((day) => (
                  <li key={day.day_number}>
                    <strong>Gün {day.day_number}:</strong> {day.city} - {day.description}
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </div>

      {/* Harita Görünümü */}
      <div className="map-container">
        <MapContainer
          center={[41.0082, 28.9784]} // İstanbul koordinatları
          zoom={3}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          {tours.map((tour) =>
            tour.days.map((day) => (
              <Marker
                key={`${tour.id}-${day.day_number}`}
                position={[day.latitude || 0, day.longitude || 0]} // Şehir koordinatları
                icon={customIcon}
              >
                <Popup>
                  <h3>{tour.name}</h3>
                  <p>Rehber: {tour.guide}</p>
                  <p>Gün: {day.day_number}</p>
                  <p>Şehir: {day.city}</p>
                  <p>Açıklama: {day.description}</p>
                </Popup>
              </Marker>
            ))
          )}
        </MapContainer>
      </div>
    </div>
  );
};

export default Map;
