import React from 'react';
import Map from './Map';
import './styles.css';

const App = ({ tours }) => {
  const today = new Date();

  return (
    <div className="app-container">
      <div className="list-container">
        <h2>Tur Listesi</h2>
        <ul>
          {tours.map((tour) => {
            const startDate = new Date(tour.start_date);
            const endDate = new Date(tour.end_date);
            const activeDayNumber = Math.ceil((today - startDate) / (1000 * 60 * 60 * 24));

            return (
              <li key={tour.id} className="tour-item">
                <h3>{tour.name}</h3>
                <p>Rehber: {tour.guide}</p>
                <p>Başlangıç Tarihi: {startDate.toLocaleDateString()}</p>
                <p>Bitiş Tarihi: {endDate.toLocaleDateString()}</p>
                {tour.days.map((day) =>
                  day.day_number === activeDayNumber ? (
                    <p key={day.day_number}>
                      <strong>Gün {day.day_number}:</strong> {day.city} - {day.description}
                    </p>
                  ) : null
                )}
              </li>
            );
          })}
        </ul>
      </div>
      <Map tours={tours} />
    </div>
  );
};

export default App;
