import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Map from './Map';
import './styles.css'; // CSS dosyasını dahil ediyoruz
// import '/App.css';

const App = () => {
  const [tours, setTours] = useState([]);
  const [cities, setCities] = useState([]);
  const [selectedTour, setSelectedTour] = useState(null);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/tours/')
      .then(response => setTours(response.data))
      .catch(error => console.error('Turlar çekilirken hata:', error));

    axios.get('http://127.0.0.1:8000/cities/')
      .then(response => setCities(response.data))
      .catch(error => console.error('Şehirler çekilirken hata:', error));
  }, []);

  return (
    <div className="app-container">
      <div className="list-container">
        <h1>JourneyVista</h1>
        <ul>
          {tours.map(tour => (
            <li
              key={tour.id}
              onClick={() => setSelectedTour(tour)}
              className={selectedTour?.id === tour.id ? 'selected-tour' : ''}
            >
              <h2>{tour.name}</h2>
              <img
                src={`http://127.0.0.1:8000${tour.image}`}
                alt={tour.name}
                className="tour-image"
              />
              <p>Rehber: {tour.guide}</p>
              <p>Başlangıç: {new Date(tour.start_date).toLocaleDateString()}</p>
              <p>Bitiş: {new Date(tour.end_date).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      </div>
      <div className="map-container">
        <Map tours={tours} cities={cities} selectedTour={selectedTour} />
      </div>
    </div>
  );
};

export default App;
