// export default Map;
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Özel Marker İkonu
const customIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/684/684908.png',
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const Map = ({ tours, cities, selectedTour }) => {
  return (
    <div className="map-wrapper">
      <MapContainer
        center={[41.0082, 28.9784]} // İstanbul merkez
        zoom={6}
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />
        {tours.map(tour => {
          const city = cities.find(c => c.name.toLowerCase() === tour.city.toLowerCase());
          if (!city) return null; // Şehir eşleşmiyorsa marker ekleme

          return (
            <Marker
              key={tour.id}
              position={[city.latitude, city.longitude]}
              icon={customIcon}
            >
              <Popup>
                <h3>{tour.name}</h3>
                <p>Rehber: {tour.guide}</p>
                <p>Başlangıç: {new Date(tour.start_date).toLocaleDateString()}</p>
                <p>Bitiş: {new Date(tour.end_date).toLocaleDateString()}</p>
              </Popup>
            </Marker>
          );
        })}
      </MapContainer>
    </div>
  );
};

export default Map;
